import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

const Experience = () => {
  const [inView, setInView] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setInView(true);
        }
      },
      { threshold: 0.1 }
    );

    const element = document.getElementById('experience');
    if (element) {
      observer.observe(element);
    }

    return () => {
      if (element) {
        observer.unobserve(element);
      }
    };
  }, []);

  const experiences = [
    {
      title: 'Full Stack Developer Intern',
      company: 'Gravatonweb Technologies',
      period: 'January 2026 - Present',
      location: 'Remote',
      description: 'Working on full-stack web development projects using modern technologies including React.js, Node.js, and Spring Boot.',
      responsibilities: [
        'Developing responsive web applications using React.js and modern JavaScript',
        'Building RESTful APIs with Node.js and Express.js',
        'Working with databases including MongoDB and MySQL',
        'Implementing authentication and security features using JWT',
        'Collaborating with cross-functional teams in an agile environment',
        'Code review and maintaining high code quality standards'
      ],
      technologies: ['React.js', 'Node.js', 'Express.js', 'MongoDB', 'MySQL', 'JWT', 'Git'],
      type: 'internship'
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  };

  const experienceVariants = {
    hidden: { opacity: 0, x: -50 },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut",
      },
    },
  };

  return (
    <section id="experience" className="py-20 lg:ml-70 relative">
      <div className="absolute inset-0 bg-hero-overlay" />
      
      <div className="container mx-auto px-6 relative z-10">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="section-title">Work Experience</h2>
          <p className="section-subtitle">
            My professional journey and internship experience
          </p>
        </motion.div>

        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
          className="max-w-4xl mx-auto"
        >
          {experiences.map((experience, index) => (
            <motion.div
              key={index}
              variants={experienceVariants}
              className="relative"
            >
              {/* Timeline Line */}
              <div className="absolute left-8 top-16 bottom-0 w-0.5 bg-electric-blue opacity-30" />
              
              {/* Timeline Dot */}
              <div className="absolute left-6 top-8 w-4 h-4 bg-electric-blue rounded-full border-4 border-deep-charcoal" />
              
              {/* Experience Card */}
              <div className="ml-16 mb-12">
                <motion.div
                  whileHover={{ y: -5 }}
                  className="card group"
                >
                  {/* Header */}
                  <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-4">
                    <div>
                      <h3 className="text-xl font-bold text-text-primary group-hover:text-electric-blue transition-colors duration-300">
                        {experience.title}
                      </h3>
                      <p className="text-lg text-electric-blue font-semibold">
                        {experience.company}
                      </p>
                    </div>
                    <div className="text-right mt-2 md:mt-0">
                      <p className="text-text-secondary font-medium">
                        {experience.period}
                      </p>
                      <p className="text-text-tertiary text-sm">
                        {experience.location}
                      </p>
                    </div>
                  </div>

                  {/* Description */}
                  <p className="text-text-secondary mb-6 leading-relaxed">
                    {experience.description}
                  </p>

                  {/* Responsibilities */}
                  <div className="mb-6">
                    <h4 className="text-text-primary font-semibold mb-3">Key Responsibilities:</h4>
                    <ul className="space-y-2">
                      {experience.responsibilities.map((responsibility, idx) => (
                        <motion.li
                          key={idx}
                          initial={{ opacity: 0, x: -20 }}
                          animate={inView ? { opacity: 1, x: 0 } : {}}
                          transition={{ duration: 0.5, delay: 0.1 * idx }}
                          className="flex items-start gap-3 text-text-secondary"
                        >
                          <i className="fas fa-chevron-right text-electric-blue text-sm mt-1 flex-shrink-0" />
                          <span>{responsibility}</span>
                        </motion.li>
                      ))}
                    </ul>
                  </div>

                  {/* Technologies */}
                  <div>
                    <h4 className="text-text-primary font-semibold mb-3">Technologies Used:</h4>
                    <div className="flex flex-wrap gap-2">
                      {experience.technologies.map((tech, techIndex) => (
                        <motion.span
                          key={tech}
                          initial={{ opacity: 0, scale: 0.8 }}
                          animate={inView ? { opacity: 1, scale: 1 } : {}}
                          transition={{ delay: 0.1 * techIndex }}
                          className="tech-tag"
                        >
                          {tech}
                        </motion.span>
                      ))}
                    </div>
                  </div>

                  {/* Experience Type Badge */}
                  <div className="absolute top-4 right-4">
                    <span className="px-3 py-1 bg-electric-blue bg-opacity-20 text-electric-blue text-xs font-semibold rounded-full border border-electric-blue border-opacity-30">
                      {experience.type.toUpperCase()}
                    </span>
                  </div>
                </motion.div>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Experience;
