import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import GeometricBackground from './GeometricBackground';

const Testimonials = () => {
  const [inView, setInView] = useState(false);
  const [currentTestimonial, setCurrentTestimonial] = useState(0);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setInView(true);
        }
      },
      { threshold: 0.1 }
    );

    const element = document.getElementById('testimonials');
    if (element) {
      observer.observe(element);
    }

    return () => {
      if (element) {
        observer.unobserve(element);
      }
    };
  }, []);

  const testimonials = [
    {
      name: 'Sarah Johnson',
      role: 'Startup Founder',
      company: 'TechStart Inc.',
      image: '/assets/img/passport-new.jpg', // Placeholder - you can add client photos later
      rating: 5,
      text: 'Pratiksha delivered an exceptional web application for our startup. Her expertise in React and Node.js helped us launch our MVP ahead of schedule. Highly professional and communicative throughout the project.',
      project: 'E-commerce Platform',
      deliveryTime: '2 weeks'
    },
    {
      name: 'Michael Chen',
      role: 'Product Manager',
      company: 'Digital Solutions Ltd.',
      image: '/assets/img/passport-new.jpg',
      rating: 5,
      text: 'Outstanding work on our company website redesign. Pratiksha understood our requirements perfectly and delivered a modern, responsive site that exceeded our expectations. Great attention to detail!',
      project: 'Corporate Website',
      deliveryTime: '1 week'
    },
    {
      name: 'Emily Rodriguez',
      role: 'Small Business Owner',
      company: 'Local Cafe Chain',
      image: '/assets/img/passport-new.jpg',
      rating: 5,
      text: 'Working with Pratiksha was a pleasure. She built a beautiful restaurant management system that streamlined our operations. Fast delivery, clean code, and excellent support. Will definitely hire again!',
      project: 'Restaurant Management System',
      deliveryTime: '3 weeks'
    },
    {
      name: 'David Kumar',
      role: 'Tech Entrepreneur',
      company: 'InnovateNow',
      image: '/assets/img/passport-new.jpg',
      rating: 5,
      text: 'Pratiksha is a talented full-stack developer who delivered our project on time and within budget. Her Java Spring Boot backend was robust and scalable. Excellent communication and problem-solving skills.',
      project: 'SaaS Application',
      deliveryTime: '4 weeks'
    }
  ];

  const stats = [
    { number: '15+', label: 'Projects Built', icon: 'fas fa-project-diagram' },
    { number: '5+', label: 'Collaborations', icon: 'fas fa-users' },
    { number: '2+', label: 'Years Learning', icon: 'fas fa-graduation-cap' },
    { number: '24/7', label: 'Learning Mode', icon: 'fas fa-brain' }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTestimonial((prev) => (prev + 1) % testimonials.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [testimonials.length]);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut",
      },
    },
  };

  return (
    <section id="testimonials" className="py-20 lg:ml-70 relative">
      <GeometricBackground opacity={0.05} color="#40e0d0" />
      <div className="absolute inset-0 bg-hero-overlay" />
      
      <div className="container mx-auto px-6 relative z-10">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="section-title">Recommendations</h2>
          <p className="section-subtitle">
            Feedback from mentors, peers, and project collaborators
          </p>
        </motion.div>

        {/* Stats Section */}
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
          className="grid grid-cols-2 md:grid-cols-4 gap-6 mb-16"
        >
          {stats.map((stat, index) => (
            <motion.div
              key={stat.label}
              variants={itemVariants}
              whileHover={{ scale: 1.05, y: -5 }}
              className="text-center p-6 bg-rich-graphite rounded-xl border border-warm-slate hover:border-electric-blue transition-all duration-300"
            >
              <div className="w-12 h-12 bg-button-gradient rounded-full flex items-center justify-center text-deep-charcoal text-lg mx-auto mb-4">
                <i className={stat.icon} />
              </div>
              <h3 className="text-2xl font-bold text-electric-blue mb-2">{stat.number}</h3>
              <p className="text-text-secondary text-sm font-medium">{stat.label}</p>
            </motion.div>
          ))}
        </motion.div>

        {/* Featured Testimonial */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.4 }}
          className="max-w-4xl mx-auto mb-16"
        >
          <div className="card relative overflow-hidden">
            <div className="absolute top-4 left-4 text-6xl text-electric-blue opacity-20">
              <i className="fas fa-quote-left" />
            </div>
            
            <div className="relative z-10 text-center pt-8">
              <motion.div
                key={currentTestimonial}
                initial={{ opacity: 0, x: 50 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -50 }}
                transition={{ duration: 0.5 }}
              >
                {/* Rating */}
                <div className="flex justify-center gap-1 mb-6">
                  {[...Array(testimonials[currentTestimonial].rating)].map((_, i) => (
                    <i key={i} className="fas fa-star text-yellow-400 text-lg" />
                  ))}
                </div>

                {/* Testimonial Text */}
                <p className="text-lg text-text-secondary leading-relaxed mb-8 italic">
                  "{testimonials[currentTestimonial].text}"
                </p>

                {/* Client Info */}
                <div className="flex flex-col md:flex-row items-center justify-center gap-6">
                  <div className="w-16 h-16 rounded-full overflow-hidden border-2 border-electric-blue">
                    <img
                      src={testimonials[currentTestimonial].image}
                      alt={testimonials[currentTestimonial].name}
                      className="w-full h-full object-cover"
                    />
                  </div>
                  
                  <div className="text-center md:text-left">
                    <h4 className="text-xl font-bold text-text-primary">
                      {testimonials[currentTestimonial].name}
                    </h4>
                    <p className="text-electric-blue font-medium">
                      {testimonials[currentTestimonial].role}
                    </p>
                    <p className="text-text-tertiary text-sm">
                      {testimonials[currentTestimonial].company}
                    </p>
                  </div>

                  <div className="text-center">
                    <p className="text-text-tertiary text-sm mb-1">Project</p>
                    <p className="text-text-primary font-semibold">
                      {testimonials[currentTestimonial].project}
                    </p>
                    <p className="text-electric-blue text-sm">
                      Delivered in {testimonials[currentTestimonial].deliveryTime}
                    </p>
                  </div>
                </div>
              </motion.div>
            </div>

            {/* Navigation Dots */}
            <div className="flex justify-center gap-2 mt-8">
              {testimonials.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentTestimonial(index)}
                  className={`w-3 h-3 rounded-full transition-all duration-300 ${
                    index === currentTestimonial
                      ? 'bg-electric-blue scale-125'
                      : 'bg-warm-slate hover:bg-electric-blue hover:opacity-70'
                  }`}
                />
              ))}
            </div>
          </div>
        </motion.div>

        {/* All Testimonials Grid */}
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
          className="grid grid-cols-1 md:grid-cols-2 gap-8"
        >
          {testimonials.map((testimonial, index) => (
            <motion.div
              key={index}
              variants={itemVariants}
              whileHover={{ y: -5 }}
              className="card group"
            >
              {/* Rating */}
              <div className="flex gap-1 mb-4">
                {[...Array(testimonial.rating)].map((_, i) => (
                  <i key={i} className="fas fa-star text-yellow-400" />
                ))}
              </div>

              {/* Testimonial Text */}
              <p className="text-text-secondary mb-6 leading-relaxed">
                "{testimonial.text}"
              </p>

              {/* Client Info */}
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 rounded-full overflow-hidden border-2 border-electric-blue">
                  <img
                    src={testimonial.image}
                    alt={testimonial.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                
                <div className="flex-1">
                  <h4 className="text-text-primary font-semibold">
                    {testimonial.name}
                  </h4>
                  <p className="text-electric-blue text-sm">
                    {testimonial.role}
                  </p>
                  <p className="text-text-tertiary text-xs">
                    {testimonial.company}
                  </p>
                </div>

                <div className="text-right">
                  <p className="text-text-primary font-semibold text-sm">
                    {testimonial.project}
                  </p>
                  <p className="text-electric-blue text-xs">
                    {testimonial.deliveryTime}
                  </p>
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* Call to Action */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.8 }}
          className="text-center mt-16"
        >
          <div className="card max-w-2xl mx-auto">
            <h3 className="text-2xl font-bold text-text-primary mb-4">
              Want to Be My Next Success Story?
            </h3>
            <p className="text-text-secondary mb-6 leading-relaxed">
              Join my satisfied clients and let's create something amazing together. 
              I'm committed to delivering high-quality solutions that exceed expectations.
            </p>
            <motion.button
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => {
                const contactSection = document.getElementById('contact');
                if (contactSection) {
                  contactSection.scrollIntoView({ behavior: 'smooth' });
                }
              }}
              className="btn-primary flex items-center justify-center gap-3 px-8 py-4 mx-auto"
            >
              <i className="fas fa-rocket" />
              <span>Start Your Project</span>
            </motion.button>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Testimonials;
